import React from 'react';
import { useState } from 'react';
import './index.css';
import Dropdown from 'react-bootstrap/Dropdown';

const api = {
    key: "5c5e122c31e64a54a2a951a385e0418d",
    base: "https://newsapi.org/v2/top-headlines",
};

function FetchData() {
    const [articles, setArticles] = useState([]);
    const [category, setCategory] = useState('general');
    const [location, setLocation] = useState('');

    const fetchArticles = (category) => {
        fetch(`${api.base}?country=us&category=${category}&pageSize=10&apiKey=${api.key}`)
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                if (data.articles) {
                    setArticles(data.articles);
                } else {
                    console.error("Error fetching articles:", data);
                }
            })
            .catch(error => {
                console.error("Error with fetch operation:", error);
            });

    };

    const handleLocationChoice = (location) => {
        setLocation(location);
    }

    const handleCategoryClick = (category) => {
        setCategory(category);
        fetchArticles(category);
    }

    return (
        <div>
            <header className="header">
                <h1>NewsApp:<br></br> CONNECTING THE WORLD</h1>
                <p> Your go-to platform for the top 10 latest headlines, <br></br> curated across various categories and your location.</p>
            </header>

            <Dropdown className='dropdown'>
                <Dropdown.Toggle variant='success' className='dropdown-basic'>
                    Dropdown Button
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    <Dropdown.Item className='dropdown-selection' onClick={() => handleLocationChoice('US')}>United States</Dropdown.Item>
                    <Dropdown.Item className='dropdown-selection' onClick={() => handleLocationChoice('India')}>India</Dropdown.Item>
                    <Dropdown.Item className='dropdown-selection' onClick={() => handleLocationChoice('')}></Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>

            <main className="categories">
                <div className="card" onClick={() => handleCategoryClick('general')}>Top 10 World News</div>
                <div className="card" onClick={() => handleCategoryClick('entertainment')}>Entertainment</div>
                <div className="card" onClick={() => handleCategoryClick('politics')}>Politics</div>
                <div className="card" onClick={() => handleCategoryClick('science')}>Science</div>
            </main>


            <button onClick={fetchArticles}>Fetch News</button>

            <footer>
                <ul>
                    {articles.map((article, index) => (
                        <li key={index}>
                            <strong>{article.title}</strong><br />
                            <a href={article.url} target="_blank" rel="noopener noreferrer">Read More</a>
                        </li>
                    ))}
                </ul>
            </footer>

        </div>
    );
}

export default FetchData;
