import React from 'react';
import { useState } from 'react';
import './index.css';

const api = {
    key: "5c5e122c31e64a54a2a951a385e0418d",
    base: "https://newsapi.org/v2/top-headlines",
};

function FetchData() {
    const [articles, setArticles] = useState([]);
    const [category, setCategory] = useState('general');

    const fetchArticles = (category) => {
        fetch(`${api.base}?country=us&category=${category}&pageSize=10&apiKey=${api.key}`)
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                if (data.articles) {
                    setArticles(data.articles);
                } else {
                    console.error("Error fetching articles:", data);
                }
            })
            .catch(error => {
                console.error("Error with fetch operation:", error);
            });

    };

    const handleCategoryClick = (category) => {
        setCategory(category);
        fetchArticles(category);
    }

    return (
        <div>


            <header className="header">
                <h1>NewsApp: CONNECTING THE WORLD</h1>
                <p>Select a category to fetch the top 10 news articles</p>
            </header>

            <main className="categories">
                <div className="card" onClick={() => handleCategoryClick('general')}>Top 10 World News</div>
                <div className="card" onClick={() => handleCategoryClick('entertainment')}>Entertainment</div>
                <div className="card" onClick={() => handleCategoryClick('politics')}>Politics</div>
                <div className="card" onClick={() => handleCategoryClick('science')}>Science</div>
            </main>


            <button onClick={fetchArticles}>Fetch News</button>

            <footer>
                <ul>
                    {articles.map((article, index) => (
                        <li key={index}>
                            <strong>{article.title}</strong><br />
                            <a href={article.url} target="_blank" rel="noopener noreferrer">Read More</a>
                        </li>
                    ))}
                </ul>
            </footer>

        </div>
    );
}

export default FetchData;
